import React, { useEffect, useState } from "react";

export default function Dashboard() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/devices")
      .then(res => res.json())
      .then(data => setDevices(data));
  }, []);

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>Technician Repair Dashboard</h1>
        <p style={styles.subtitle}>
          Overview of devices currently under repair
        </p>

        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Device</th>
              <th style={styles.th}>Issue</th>
              <th style={styles.th}>Status</th>
            </tr>
          </thead>
          <tbody>
            {devices.length === 0 ? (
              <tr>
                <td colSpan="3" style={styles.empty}>
                  No repair records found
                </td>
              </tr>
            ) : (
              devices.map((d, i) => (
                <tr key={i} style={styles.tr}>
                  <td style={styles.td}>{d.model}</td>
                  <td style={styles.td}>{d.issue}</td>
                  <td style={{ ...styles.td, ...statusStyle(d.status) }}>
                    {d.status}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/* ===== STYLES ===== */

const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #eef2f3, #d9e2ec)",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    padding: "40px",
    fontFamily: "Segoe UI, sans-serif"
  },
  card: {
    width: "100%",
    maxWidth: "900px",
    background: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 10px 30px rgba(0,0,0,0.1)",
    padding: "30px"
  },
  title: {
    margin: 0,
    fontSize: "26px",
    color: "#1f2937"
  },
  subtitle: {
    marginTop: "6px",
    marginBottom: "24px",
    color: "#6b7280"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse"
  },
  th: {
    textAlign: "left",
    padding: "12px",
    backgroundColor: "#f3f4f6",
    color: "#374151",
    fontWeight: "600",
    borderBottom: "2px solid #e5e7eb"
  },
  tr: {
    transition: "background 0.2s ease"
  },
  td: {
    padding: "12px",
    borderBottom: "1px solid #e5e7eb",
    color: "#374151"
  },
  empty: {
    textAlign: "center",
    padding: "20px",
    color: "#9ca3af"
  }
};

function statusStyle(status) {
  switch (status?.toLowerCase()) {
    case "completed":
      return { color: "#16a34a", fontWeight: "600" };
    case "pending":
      return { color: "#ca8a04", fontWeight: "600" };
    case "in progress":
      return { color: "#2563eb", fontWeight: "600" };
    default:
      return {};
  }
}
