import React, { useState } from "react";
import Navbar from "./Navbar"; // Import your new file
import RepairTable from "./RepairTable"; // Recommended: move the table to its own file too!

export default function Dashboard() {
  const [active, setActive] = useState("repairs");

  // Logic to switch content based on the active state
  const renderContent = () => {
    switch (active) {
      case "repairs":
        return <RepairTable />; 
      case "stock":
        return <div><h2>Inventory Management</h2><p>Stock levels here...</p></div>;
      case "analytics":
        return <div><h2>Performance Analytics</h2><p>Charts and graphs here...</p></div>;
      default:
        return <RepairTable />;
    }
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f1f5f9" }}>
      <Navbar active={active} setActive={setActive} />
      <div style={{ padding: "40px", display: "flex", justifyContent: "center" }}>
        <div style={styles.card}>
           {renderContent()}
        </div>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: "100%",
    maxWidth: "1000px",
    background: "#ffffff",
    borderRadius: "12px",
    padding: "30px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.08)"
  }
};
