import React, { useState } from "react";
import Navbar from "./Navbar";
import RepairTable from "./RepairTable";
import Stock from "./Stock";
import Analytics from "./Analytics";

export default function Dashboard() {
  const [active, setActive] = useState("repairs");

  const handleLogout = () => {
    // Logic for Logout -> End flow from flowchart
    console.log("Logging out...");
    window.location.href = "/login"; 
  };

  const renderContent = () => {
    switch (active) {
      case "repairs":
        // This covers the central branch: Add/Edit Details -> Save Record
        return <RepairTable />; 
      case "stock":
        // This covers the left branch: Check Stock -> Alerts -> Manage Stock
        return <Stock />;
      case "analytics":
        // This covers the right branch: Weekly/Monthly Reports
        return <Analytics />;
      case "profile":
        return (
          <div style={styles.profileView}>
            <h2>Technician Profile</h2>
            <p>Edit Information</p>
            <button style={styles.logoutBtn} onClick={handleLogout}>Logout</button>
          </div>
        );
      default:
        return <RepairTable />;
    }
  };

  return (
    <div style={styles.page}>
      <Navbar active={active} setActive={setActive} onLogout={handleLogout} />
      <div style={styles.container}>
        <div style={styles.card}>
           {renderContent()}
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: { 
    minHeight: "100vh", 
    background: "#f1f5f9",
    fontFamily: "Inter, system-ui, sans-serif"
  },
  container: { 
    padding: "40px", 
    display: "flex", 
    justifyContent: "center" 
  },
  card: {
    width: "100%",
    maxWidth: "1100px",
    background: "#ffffff",
    borderRadius: "12px",
    padding: "30px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.08)"
  },
  profileView: {
    textAlign: "center",
    padding: "20px"
  },
  logoutBtn: {
    marginTop: "20px",
    padding: "10px 20px",
    background: "#ef4444",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "bold"
  }
};
